              countries.push(fields[3]);
              countryPeople[fields[3]] = [];
              countryPeople[fields[3]].push(fields[0]);
            }
          }
        });
        resolve(countryPeople);
      } else {
        reject('Cannot load the database');
      }
    });
  });
}
